import React, {useState} from "react";
import "../styles/slideshow.css"
import {NextSlide, PrevSlide, CurrentSlide} from "./slide";
import "../styles/slide.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"
import "slick-carousel/slick/slick-theme.css"

export const slidesContext = React.createContext();

function Slideshow() {

    /* const [currentIndex, setCurrentIndex] = useState(0)

     const goToPrevious = () => {
         const isFirstSlide = currentIndex === 0;
         const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1
         setCurrentIndex(newIndex);

     }
     const goToNext = () => {
         const isLastSlide = currentIndex === slides.length - 1;
         const newIndex = isLastSlide ? 0 : currentIndex + 1
         setCurrentIndex(newIndex);

     }*/
    let settings = {
        dot: true, infinite: true, speed: 500, slidesToShow: 2, slidesToScroll: 1, cssEase: "linear"
    }
    return <div style={{maxWidth: "100vw", overflow: "hidden"}}>
        <Slider {...settings}>
            <div>
                <h3>1</h3>
            </div>
            <div>
                <h3>2</h3>
            </div>
            <div>
                <h3>3</h3>
            </div>
            <div>
                <h3>4</h3>
            </div>
            <div>
                <h3>5</h3>
            </div>
            <div>
                <h3>6</h3>
            </div>
        </Slider>
        );
        }
    </div>
}

export default Slideshow;
