import "../styles/tab.css";
import $ from "jquery"

let page;
$(
    $(".field-1").on("mouseover", function() {
        $(".field-2").show();
    }).on("mouseout", function() {
        $(".field-2").hide();
    })
)

const arrow = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                   className="chevron-down" viewBox="0 0 16 16">
    <path fillRule="evenodd"
          d="M1.646 4.646a.5.5 0 0 1 .708 0L8 10.293l5.646-5.647a.5.5 0 0 1 .708.708l-6 6a.5.5 0 0 1-.708 0l-6-6a.5.5 0 0 1 0-.708z"/>
</svg>

function Tab(props) {
    return <div className="tab">{props.page}</div>
}

function SubNavTab(props) {
    return <div>
        <div className="subnav tab">
            {props.page} {arrow}
        </div><div className="subnav-content">
        <a data-page="about" >SOFTWARE</a><br/>
        <a data-page="about" >INTELIGENCIA ARTIFICIAL</a><br/>
        <a data-page="about" >CIENCIA DE DATOS</a><br/>
        <a data-page="about" >CYBERSEGURIDAD</a><br/>
    </div>
    </div>
}

export {SubNavTab, Tab};
