import "../styles/slide.css";


function CurrentSlide(props){

    return <div className="current-slide move">
        <img className="slide-img" src={props.img} alt="EPN"/>
        <div className="slide-content">
            <h2>Lorem</h2>
            <a>Epaaa</a>
        </div>

       {/* <a className="slideshow-circles">○ ⬤ ○ ○ ○</a>*/}
    </div>
}

function PrevSlide(props) {

    return <div className="prev-slide">
        <img className="slide-img move" src={props.img} alt="EPN"/>
        <div className="slide-content">
            <h2>Lorem</h2>
            <a>Epaaa</a>
        </div>

    </div>
}

function NextSlide(props) {

    return <div className="next-slide move">
        <img className="slide-img" src={props.img} alt="EPN"/>
        <div className="slide-content">
            <h2>Lorem</h2>
            <a>Epaaa</a>
        </div>
    </div>
}

export {NextSlide, PrevSlide, CurrentSlide}
