import React, {useState} from "react";
import "../styles/slideshow.css"
import {NextSlide, PrevSlide, CurrentSlide} from "./slide";
import "../styles/slide.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"
import "slick-carousel/slick/slick-theme.css"

export const slidesContext = React.createContext();

function Slideshow() {

    /* const [currentIndex, setCurrentIndex] = useState(0)

     const goToPrevious = () => {
         const isFirstSlide = currentIndex === 0;
         const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1
         setCurrentIndex(newIndex);

     }
     const goToNext = () => {
         const isLastSlide = currentIndex === slides.length - 1;
         const newIndex = isLastSlide ? 0 : currentIndex + 1
         setCurrentIndex(newIndex);

     }*/
    let settings = {
        dot: true, autoplay: true, infinite: true, speed: 500, slidesToShow: 3, slidesToScroll: 1, cssEase: "linear"
    }
    return <div style={{maxWidth: "100vw", overflow: "hidden"}}>
        <Slider {...settings}>
            <div className="slick-track">
                {/*<slidesContext.Provider value={currentIndex}>*/}
                <a className="prev">&#10094;</a>
                <a className="next">&#10095;</a>

                <CurrentSlide img="https://i.ytimg.com/vi/FF7RFvw6mrU/hq720.jpg"/>
                <CurrentSlide img="http://www.epn.edu.ec/wp-content/uploads/2015/06/sistemas-epn.jpg"/>
                <CurrentSlide img="https://i.kym-cdn.com/photos/images/facebook/002/421/993/372.jpg"/>
                <CurrentSlide img="http://www.epn.edu.ec/wp-content/uploads/2015/04/EPN-TUB.jpg"/>
                <CurrentSlide img="https://cdn-3.expansion.mx/dims4/default/c28efa4/2147483647/strip/true/crop/843x526+0+0/resize/1200x749!/format/webp/quali*/}ty/90/?url=https%3A%2F%2Fcdn-3.expansion.mx%2F98%2F12%2Ff6d3cef14c128dca115380c073cf%2Fcuartoscuro-669567-digital.jpeg"/>

            </div>
        </Slider>
    </div>
}

export default Slideshow;
