import React, {useContext, useState} from "react";
import "../styles/slide.css";
import {slidesContext} from "./slideshow";


const slides=[
    {url: "https://i.ytimg.com/vi/FF7RFvw6mrU/hq720.jpg"},
    {url: "http://www.epn.edu.ec/wp-content/uploads/2015/06/sistemas-epn.jpg"},
    {url: "https://media.tenor.com/ffEjNC5VFO4AAAAC/thanos-dancing.gif"},
    {url: "https://i.kym-cdn.com/photos/images/facebook/002/421/993/372.jpg"},

];

function CurrentSlide(){

    const currentIndex = useContext(slidesContext)

    return <div className="current-slide">
        <img className="slide-img" src={slides[currentIndex].url} alt="EPN"/>
        <div className="slide-content">
            <h2>Lorem</h2>
            <a>Epaaa</a>
        </div>

        <a className="slideshow-circles">○ ⬤ ○ ○ ○</a>
    </div>
}

function PrevSlide() {
    const [currentIndex] = useState(0)
    const slides = [
        {url: "https://i.ytimg.com/vi/FF7RFvw6mrU/hq720.jpg"},
        {url: "http://www.epn.edu.ec/wp-content/uploads/2015/06/sistemas-epn.jpg"},
        {url: "https://media.tenor.com/ffEjNC5VFO4AAAAC/thanos-dancing.gif"},
        {url: "https://i.kym-cdn.com/photos/images/facebook/002/421/993/372.jpg"},
    ];
    return <div className="prev-slide">
        <img className="slide-img" src={slides[currentIndex].url} alt="EPN"/>
        <div className="slide-content">
            <h2>Lorem</h2>
            <a>Epaaa</a>
        </div>

    </div>
}

function NextSlide() {
    const [currentIndex] = useState(2)

    return <div className="next-slide">
        <img className="slide-img" src={slides[currentIndex].url} alt="EPN"/>
        <div className="slide-content">
            <h2>Lorem</h2>
            <a>Epaaa</a>
        </div>
    </div>
}

export {NextSlide, PrevSlide, CurrentSlide}
