import React, {useState} from "react";
import "../styles/slideshow.css"
import {NextSlide, PrevSlide, CurrentSlide} from "./slide";
import "../styles/slide.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"
import "slick-carousel/slick/slick-theme.css"

export const slidesContext = React.createContext();

function Slideshow() {

    /* const [currentIndex, setCurrentIndex] = useState(0)

     const goToPrevious = () => {
         const isFirstSlide = currentIndex === 0;
         const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1
         setCurrentIndex(newIndex);

     }
     const goToNext = () => {
         const isLastSlide = currentIndex === slides.length - 1;
         const newIndex = isLastSlide ? 0 : currentIndex + 1
         setCurrentIndex(newIndex);

     }*/
    let settings = {
        dots: true,
        infinite: true,
        autoplay: true,
        autoplaySpeed: 2000,
        speed: 600,
        slidesToShow: 1,
        centerMode: true,
        variableWidth: true
    }
    return <div className="slide-show">
        <Slider {...settings}>

            <CurrentSlide img="https://i.ytimg.com/vi/FF7RFvw6mrU/hq720.jpg"/>
            <CurrentSlide img="http://www.epn.edu.ec/wp-content/uploads/2015/06/sistemas-epn.jpg"/>
            <CurrentSlide img="https://i.kym-cdn.com/photos/images/facebook/002/421/993/372.jpg"/>
            <CurrentSlide img="http://www.epn.edu.ec/wp-content/uploads/2015/04/EPN-TUB.jpg"/>
            <CurrentSlide img="https://i.ytimg.com/vi/C6RUp21s6BQ/maxresdefault.jpg"/>

        </Slider>

        <a className="prev">&#10094;</a>
        <a className="next">&#10095;</a>
    </div>
}

export default Slideshow;
