import React, {useState} from "react";
import "../styles/slideshow.css"
import {NextSlide, PrevSlide, CurrentSlide} from "./slide";
import "../styles/slide.css";
export const slidesContext = React.createContext();
const slides=[
    {url: "https://i.ytimg.com/vi/FF7RFvw6mrU/hq720.jpg"},
    {url: "http://www.epn.edu.ec/wp-content/uploads/2015/06/sistemas-epn.jpg"},
    {url: "https://media.tenor.com/ffEjNC5VFO4AAAAC/thanos-dancing.gif"},
    {url: "https://i.kym-cdn.com/photos/images/facebook/002/421/993/372.jpg"},

];

function Slideshow() {

    const [currentIndex, setCurrentIndex] = useState("initialState")

    const handleChange = (e) => {
        setCurrentIndex(e.target.currentIndex);
    };


    const goToPrevious = () => {
        const isFirstSlide = currentIndex === 0;
        const newIndex = isFirstSlide ? slides.length - 1: currentIndex-1
        setCurrentIndex(newIndex);
    }
    const goToNext = () => {
        const isLastSlide = currentIndex === slides.length - 1;
        const newIndex = isLastSlide ? 0: currentIndex+1
        setCurrentIndex(newIndex);
    }

    return <div className="slide-show">
        <slidesContext.Provider value={currentIndex}>
            <a className="prev" onClick={goToPrevious} >&#10094;</a>
            <a className="next" onClick={goToNext}>&#10095;</a>
            <PrevSlide currentIndex={currentIndex} handleChange={handleChange}/>
            <CurrentSlide currentIndex={currentIndex} handleChange={handleChange}/>
            <NextSlide currentIndex={currentIndex} handleChange={handleChange}/>
        </slidesContext.Provider>
    </div>

}

export default Slideshow;
